function changeCartQty(id, delta) {
    id = Number(id);
    const item = cart.find(it => it.id === id);
    if (!item) return;

    item.qty = (item.qty || 1) + delta;

    if (item.qty <= 0) {
        // —É–±—Ä–∞—Ç—å –∏–∑ –∫–æ—Ä–∑–∏–Ω—ã, –µ—Å–ª–∏ —Å—Ç–∞–ª–æ 0
        cart = cart.filter(it => it.id !== id);
    }

    saveCart();
    renderCartModal();
}

// –ø–æ–ª–Ω–æ–µ —É–¥–∞–ª–µ–Ω–∏–µ –ø–æ–∑–∏—Ü–∏–∏ (–ø–æ –∫—Ä–µ—Å—Ç–∏–∫—É –≤ –∫–æ—Ä–∑–∏–Ω–µ)
function removeFromCart(id) {
    id = Number(id);
    cart = cart.filter(item => item.id !== id);
    saveCart();
    renderCartModal();
}

// –æ—Ç—Ä–∏—Å–æ–≤–∫–∞ –∫–Ω–æ–ø–æ–∫ –Ω–∞ –∫–∞—Ä—Ç–æ—á–∫–∞—Ö: "–î–æ–±–∞–≤–∏—Ç—å" –∏–ª–∏ "- 3 +"
function renderCartButtonsState() {
    document.querySelectorAll('.card').forEach(card => {
        const id = parseInt(card.dataset.id);
        if (!id || id >= 101) return; // –≤ –µ–¥–µ –∫–æ—Ä–∑–∏–Ω—ã –Ω–µ—Ç

        const btn = card.querySelector('.card-footer .btn-main');
        if (!btn) return;

        const item = cart.find(it => it.id === id);

        if (!item) {
            // —Ç–æ–≤–∞—Ä–∞ –Ω–µ—Ç –≤ –∫–æ—Ä–∑–∏–Ω–µ ‚Äî –æ–±—ã—á–Ω–∞—è –∫–Ω–æ–ø–∫–∞
            btn.innerHTML = '–î–æ–±–∞–≤–∏—Ç—å –≤ –∫–æ—Ä–∑–∏–Ω—É';
            btn.onclick = function (e) {
                e.stopPropagation();
                addToCart(id);
            };
        } else {
            const qty = item.qty || 1;

            btn.innerHTML = `
                <div class="qty-control">
                    <button type="button" class="qty-btn qty-minus">‚àí</button>
                    <span class="qty-value">${qty}</span>
                    <button type="button" class="qty-btn qty-plus">+</button>
                </div>
            `;
            // –æ—Ç–∫–ª—é—á–∞–µ–º –∫–ª–∏–∫ –ø–æ –≤—Å–µ–π –∫–Ω–æ–ø–∫–µ
            btn.onclick = function (e) {
                e.stopPropagation();
            };

            const minus = btn.querySelector('.qty-minus');
            const plus = btn.querySelector('.qty-plus');

            minus.onclick = function (e) {
                e.stopPropagation();
                changeCartQty(id, -1);
            };
            plus.onclick = function (e) {
                e.stopPropagation();
                changeCartQty(id, 1);
            };
        }
    });
}

