        function initHeroAnimation() {
    const hero = document.querySelector('.hero');
    if (!hero) return;

    if (!('IntersectionObserver' in window)) {
        hero.classList.add('hero-animate');
        return;
    }

    const observer = new IntersectionObserver(
        (entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    hero.classList.add('hero-animate');
                } else {
                    hero.classList.remove('hero-animate');
                }
            });
        },
        { threshold: 0.4 }
    );

    observer.observe(hero);
}

/* –ê–Ω–∏–º–∞—Ü–∏—è –ø–æ—è–≤–ª–µ–Ω–∏—è –¥–ª—è –≤—Å–µ—Ö section-header –∏ –∫–∞—Ä—Ç–æ—á–µ–∫ .card */
function initScrollAnimations() {
    const elements = document.querySelectorAll('.section-header, .card');
    if (!elements.length) return;

    // –Ω–∞–≤–µ—à–∏–≤–∞–µ–º –±–∞–∑–æ–≤—ã–π –∫–ª–∞—Å—Å –∞–Ω–∏–º–∞—Ü–∏–∏
    elements.forEach(el => el.classList.add('animate-on-scroll'));

    // –µ—Å–ª–∏ IntersectionObserver –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è ‚Äî –ø—Ä–æ—Å—Ç–æ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å—ë
    if (!('IntersectionObserver' in window)) {
        elements.forEach(el => el.classList.add('in-view'));
        return;
    }

    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('in-view');
            } else {
                // —É–±–∏—Ä–∞–µ–º –∫–ª–∞—Å—Å, —á—Ç–æ–±—ã –ø—Ä–∏ –≤–æ–∑–≤—Ä–∞—â–µ–Ω–∏–∏ –∞–Ω–∏–º–∞—Ü–∏—è –ø–æ–≤—Ç–æ—Ä—è–ª–∞—Å—å
                entry.target.classList.remove('in-view');
            }
        });
    }, { threshold: 0.25 });

    elements.forEach(el => observer.observe(el));
}
