// –ü–ª–∞–≤–Ω–æ–µ "–ø–µ—á–∞—Ç–∞–Ω–∏–µ" —Ç–µ–∫—Å—Ç–∞ –ø–æ –±—É–∫–≤–∞–º
function typeText(element, text, speed, callback) {
    let i = 0;
    element.textContent = '';

    function step() {
        if (i < text.length) {
            element.textContent += text.charAt(i);
            i++;
            setTimeout(step, speed);
        } else if (typeof callback === 'function') {
            callback();
        }
    }

    step();
}
