/* ==== –°–±—Ä–æ—Å —Å–æ—Å—Ç–æ—è–Ω–∏—è –±–ª–æ–∫–∞ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤ –≤ –º–æ–¥–∞–ª–∫–µ ==== */
function resetEventToolsUI() {
    const contentEl = document.getElementById('eventToolsContent');
    if (contentEl) {
        contentEl.innerHTML = `
<p style="font-size: 14px; color: #666;">
    –í—ã–±–µ—Ä–∏—Ç–µ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏ –ø–æ —ç—Ç–æ–º—É –º–µ—Å—Ç—É.
</p>`;
    }
    const buttons = document.querySelectorAll('#eventToolsButtons button');
    buttons.forEach(btn => btn.classList.remove('active'));
}

/* ==== –ü–æ–∫–∞–∑ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞ (—Ä—é–∫–∑–∞–∫ / —Å–≤—è–∑—å / –ø–æ–≥–æ–¥–∞) ==== */
function showEventTool(toolKey) {
    const contentEl = document.getElementById('eventToolsContent');
    if (!contentEl) return;

    const regionKey = currentEventRegion;
    const regionTools = regionKey ? eventToolsData[regionKey] : null;
    const toolHtml = regionTools && regionTools[toolKey]
        ? regionTools[toolKey]
        : '<p style="font-size: 14px; color: #666;">–î–ª—è —ç—Ç–æ–≥–æ –æ–±—ä–µ–∫—Ç–∞ –µ—â—ë –Ω–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω—ã —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏.</p>';

    contentEl.innerHTML = toolHtml;

    // –ü–æ–¥—Å–≤–µ—Ç–∫–∞ –∞–∫—Ç–∏–≤–Ω–æ–π –∫–Ω–æ–ø–∫–∏
    const buttons = document.querySelectorAll('#eventToolsButtons button');
    buttons.forEach(btn => {
        const isActive = btn.getAttribute('data-tool') === toolKey;
        if (isActive) btn.classList.add('active');
        else btn.classList.remove('active');
    });
}

/* ==== –ü—Ä–∏–≤—è–∑–∫–∞ –∫–∞—Ä—Ç–æ—á–µ–∫ –∫ —Ä–µ–≥–∏–æ–Ω–∞–º ==== */
document.addEventListener('DOMContentLoaded', function () {
    // –í—Å–µ –∫–∞—Ä—Ç–æ—á–∫–∏ —Å data-id (–∏ –≥–æ—Ä–Ω—ã–µ, –∏ –º—É–∑–µ–∏ –∏ —Ç.–¥.)
    const cards = document.querySelectorAll('.card[data-id]');

    cards.forEach(card => {
        const category = card.getAttribute('data-category') || '';
        const regionKey = eventRegionByCategory[category];

        card.addEventListener('click', function () {
            // –ï—Å–ª–∏ —ç—Ç–æ –≥–æ—Ä–Ω—ã–π –æ–±—ä–µ–∫—Ç ‚Äî –ø—Ä–∏–≤—è–∑—ã–≤–∞–µ–º –∫ —Ä–µ–≥–∏–æ–Ω—É
            if (regionKey) {
                currentEventRegion = regionKey;
                currentEventId = card.getAttribute('data-id');
            } else {
                // –î–ª—è –º—É–∑–µ–µ–≤/–∞—Ä—Ö–∏—Ç–µ–∫—Ç—É—Ä—ã –∏ —Ç.–ø. ‚Äî —Å–±—Ä–∞—Å—ã–≤–∞–µ–º —Ä–µ–≥–∏–æ–Ω
                currentEventRegion = null;
                currentEventId = null;
            }
            // –ö–∞–∂–¥—ã–π —Ä–∞–∑ –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏ "–ü–æ–¥—Ä–æ–±–Ω–µ–µ" —Ö–æ—Ç–∏–º —á–∏—Å—Ç—ã–π –≤–∏–¥ –±–ª–æ–∫–∞
            resetEventToolsUI();
        });
    });
});